import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import {
  Card,
  CardBody,
  CardLink,
  CardSubtitle,
  CardText,
  Navbar,
  Spinner,
} from "reactstrap";
import "./FilterAddmember.css";

function FilterAddmember({ data, DeleteBTN }) {
  const [input, setinput] = useState("");
  const [dataFiltered, setdataFiltered] = useState([]);

  useEffect(() => {
    if (data) {
      let mynewdata = data.filter((v) => {
        return v.name.toLowerCase().includes(input);
      });
      setdataFiltered(mynewdata);
    }
  }, [data, input]);

  const Handelinput = (e) => {
    setinput(e.target.value.toLowerCase());
  };

  return (
    <div>
      <input type="search" value={input} onChange={Handelinput} />

      <div className="FilterAddmember">
        {console.log(data)}
        <Navbar className="my-1" color="dark" dark>
          <Link to="/">Back to Home</Link>
        </Navbar>

        <div className="container">
          {data == null ? (
            <Spinner size="sm">
              <h3>Loading...Data</h3> <br /> <h4>No data To show</h4>
            </Spinner>
          ) : (
            <div>
              <ul className="unOrderList">
                {dataFiltered.map((v, i) => (
                  <Card key={i} className="card">
                    <CardBody>
                      <img alt="Card cap" className="img" src={v.pic} />
                      <CardText className="name">{v.name}</CardText>
                      <CardText className="account">{v.Account}</CardText>

                      <CardSubtitle className="sfid" tag="h6">
                        {v.sfid}
                      </CardSubtitle>

                      <CardText className="email">{v.email}</CardText>

                      <CardLink
                        className="delete-btn"
                        onClick={() => DeleteBTN(v)}
                      >
                        Delete
                      </CardLink>
                    </CardBody>
                  </Card>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default FilterAddmember;
