import React, { useState } from "react";
function JuniperREF({ data }) {
  const [input, setinput] = useState("");
  const [FilterInput, SetfilterInput] = useState(data);

  const handleSearch = (e) => {
    setinput(e.target.value);
    const FilterJunbier =
      input === ""
        ? data
        : data.msg.filter((v) => {
            return v.JuniperREF.includes(input);
          });
    SetfilterInput(FilterJunbier);
  };
  return (
    <div>
      <input type="search" value={input} onChange={handleSearch} />
      {console.log(FilterInput)}
    </div>
  );
}

export default JuniperREF;
