import { React, useState } from "react";

function FilterByTicketNumber({ data }) {
  const [val, setVal] = useState("");
  const [filtered, setFiltered] = useState(data);

  const handleSearch = (e) => {
    const value = e.target.value;
    setVal(value);

    const filterTkt =
      value === ""
        ? data
        : data.filter((v) => String(v.TicketNumber) === value);

    setFiltered(filterTkt);
  };

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", options);
  };

  return (
    <div>
      <input type="search" value={val} onChange={handleSearch} />
      {filtered &&
        filtered.map((v, i) => (
          <tr key={i}>
            <th scope="row" className="HeadNum">
              {i + 1}
            </th>
            <td>{v.TicketNumber}</td>

            <td>{v.AgentName}</td>
            <td>{v.ProductType}</td>
            <td>{v.GuestName}</td>
            <td>{v.Supplier}</td>
            <td>{v.SupplierCategory}</td>
            <td>{v.JuniperREF}</td>
            <td>{v.SupplierReference}</td>
            <td>{formatDate(v.CheckInDate)}</td>
            <td>{formatDate(v.CheckOutDate)}</td>
            <td>{v.Vouchernumber}</td>
            <td>{v.SalesRate}</td>
            <td>{v.NetRate}</td>
            <td>{v.Currency}</td>
            <td>{v.ExchangeRate}</td>
            <td>{v.NetRateINKWD}</td>
            <td>{v.Profit}</td>
            <td>{v.Remarks}</td>
          </tr>
        ))}
    </div>
  );
}

export default FilterByTicketNumber;
