import React, { useEffect, useState } from "react";
import CardComponent from "./CardComponent";
import "../App.css";

function TeamComponent({ data }) {
  const [selected, Setselected] = useState("");
  const [state, Setstate] = useState([]);
  // console.log(data);
  // useEffect(() => {
  //   fetch("https://fakerapi.it/api/v1/books")
  //     .then((data) => data.json())
  //     .then((data) => {
  //       Setstate(data);
  //     });

  // }, []);/
  const selectedFunc = (e) => {
    Setselected(e.target.value);
  };

  return (
    <div>
      <select onChange={selectedFunc}>
        <option>Antonina Ratke</option>
        <option>Archibald Daniel</option>
        <option>Candelario Schuster</option>
        <option>Harvey Murazik</option>
        <option>Christophe Kunze</option>
      </select>
      <CardComponent data={state} Setdata={Setstate} />

      {state.length === 0 ? (
        <h3>no items to show </h3>
      ) : (
        <div>
          {state.map((v) => (
            <div>{v.title}</div>
          ))}
        </div>
      )}
    </div>
  );
}

export default TeamComponent;
