/* eslint-disable react-hooks/rules-of-hooks */
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import {
  VerticalTimeline,
  VerticalTimelineElement,
  StarIcon,
  SchoolIcon,
  WorkIcon,
} from "react-vertical-timeline-component";
import { Card, CardBody, CardImg, CardText, CardTitle } from "reactstrap";
import "./productStyle.css";
function ProductDetails() {
  const [Mycard, setMycard] = useState(null);
  // const [Comment, setComment] = useState(null);
  const ProductID = useParams();

  console.log(ProductID);
  useEffect(() => {
    axios
      .post(`http://localhost:9000/item/Myid`, { ProductID })
      .then((res) => setMycard(res));
  }, [ProductID]);

  return (
    <div className="ProductDiv">
      {Mycard !== null ? (
        // <Card
        //   style={{
        //     width: "48rem",
        //     height: "30rem",
        //     backgroundColor: "black",
        //     margin: "5px",
        //     color: "aqua",
        //     wordBreak: "break-word",
        //     textAlign: "center",
        //     fontFamily: "serif",
        //     fontSize: "15px",
        //     borderRadius: "20px",
        //     border: "2px solid gray",
        //   }}
        // >
        //   <CardBody className="CardBody">
        //     <CardTitle>{Mycard.data.title}</CardTitle>
        //     <CardText>{Mycard.data.price}</CardText>
        //     <CardText>{Mycard.data.description}</CardText>
        //   </CardBody>
        // </Card>
        <VerticalTimeline>
          <VerticalTimelineElement
            className="vertical-timeline-element--work"
            contentStyle={{ background: "rgb(33, 150, 243)", color: "#fff" }}
            contentArrowStyle={{ borderRight: "7px solid  rgb(33, 150, 243)" }}
            date="2011 - present"
            iconStyle={{ background: "rgb(33, 150, 243)", color: "#fff" }}
            icon={<WorkIcon />}
          >
            <h3 className="vertical-timeline-element-title">
              Creative Director
            </h3>
            <h4 className="vertical-timeline-element-subtitle">Miami, FL</h4>
            <p>
              Creative Direction, User Experience, Visual Design, Project
              Management, Team Leading
            </p>
          </VerticalTimelineElement>
          <VerticalTimelineElement
            className="vertical-timeline-element--work"
            date="2010 - 2011"
            iconStyle={{ background: "rgb(33, 150, 243)", color: "#fff" }}
            icon={<WorkIcon />}
          ></VerticalTimelineElement>
        </VerticalTimeline>
      ) : (
        <h3>No data Found for this </h3>
      )}
    </div>
  );
}

export default ProductDetails;
