import React, { useState } from "react";
import { Link } from "react-router-dom";
import {
  Card,
  CardBody,
  CardLink,
  CardSubtitle,
  CardText,
  CardTitle,
} from "reactstrap";

function FilteredProduct({ data }) {
  const [input, setiunput] = useState("");
  const [mydata, setmydata] = useState(data);

  const HandelSearch = (e) => {
    setiunput(e.target.value);

    if (mydata) {
      const filteredItems = data.msg.filter((v) => {
        return v.description.includes(input);
      });
      console.log(filteredItems);
      setmydata(filteredItems);
    }
  };
  return (
    <div>
      <input type="search" value={input} onChange={HandelSearch} />
      {mydata === null && <h3>no data </h3>}
      {mydata &&
        mydata.msg.map((v, i) => (
          <Card
            key={i}
            style={{
              width: "21rem",
              height: "30rem",
              backgroundColor: "black",
              margin: "5px",
              color: "aqua",
              wordBreak: "break-word",
              textAlign: "center",
              fontFamily: "serif",
              fontSize: "15px",
              borderRadius: "20px",
              border: "2px solid gray",
            }}
          >
            <CardBody>
              <CardTitle tag="h5">#{i + 1}</CardTitle>
              <CardSubtitle className="mb-2 text-muted" tag="h6">
                {v.full_name}
              </CardSubtitle>
            </CardBody>

            <CardBody>
              <CardText
                style={{
                  color: "whitesmoke",
                  wordBreak: "break-word",
                  textAlign: "center",
                  fontFamily: "serif",
                  fontSize: "15px",
                }}
              >
                {v.title}
              </CardText>
              {/* {v.image.length === 0 ? (
              <img src={imgUrl} alt="Card cap" className="img" />
            ) : (
              <img src={v.image} alt="Card cap" className="img" />
            )} */}

              <CardText>{v.description}</CardText>
              <CardLink style={{ color: "gray" }}>
                <Link to={`/${v.Myid}`} className="btn btn-primary">
                  Go Details
                </Link>
              </CardLink>

              <CardText> createdOn :{v.createdOn}</CardText>

              {/* <button onClick={() => DeleteItem(v)}>Delete</button> */}
            </CardBody>
          </Card>
        ))}
    </div>
  );
}

export default FilteredProduct;
