import React, { useState } from "react";
import { Link } from "react-router-dom";
import {
  Card,
  CardBody,
  CardLink,
  CardSubtitle,
  CardText,
  CardTitle,
} from "reactstrap";
import "../component/ProductFilterStyle.css";
function FilteredProduct({ data }) {
  const [input, setInput] = useState("");
  const [myData, setMyData] = useState(data);

  const handleSearch = (e) => {
    const inputValue = e.target.value;
    setInput(inputValue);

    if (data) {
      const filteredItems = data.msg.filter((item) =>
        item.title.toLowerCase().includes(inputValue.toLowerCase())
      );
      setMyData({ msg: filteredItems });
    }
  };

  return (
    <div>
      <input type="search" value={input} onChange={handleSearch} />
      {myData === null && <h3>No data found</h3>}
      {myData &&
        myData.msg.map((item, i) => (
          <ul className="unOrderList">
            <Card
              key={i}
              style={{
                width: "21rem",
                height: "30rem",
                backgroundColor: "black",
                margin: "5px",
                color: "aqua",
                wordBreak: "break-word",
                textAlign: "center",
                fontFamily: "serif",
                fontSize: "15px",
                borderRadius: "20px",
                border: "2px solid gray",
                flex: "1 0 21rem",
              }}
            >
              <CardBody>
                <CardTitle tag="h5">#{i + 1}</CardTitle>
                <CardSubtitle className="mb-2 text-muted" tag="h6">
                  {item.full_name}
                </CardSubtitle>
              </CardBody>

              <CardBody>
                <CardText
                  style={{
                    color: "whitesmoke",
                    wordBreak: "break-word",
                    textAlign: "center",
                    fontFamily: "serif",
                    fontSize: "15px",
                  }}
                >
                  {item.title}
                </CardText>

                <CardText>{item.description}</CardText>
                <CardLink style={{ color: "gray" }}>
                  <Link to={`/${item.Myid}`} className="btn btn-primary">
                    Go Details
                  </Link>
                </CardLink>

                <CardText> createdOn :{item.createdOn}</CardText>
              </CardBody>
            </Card>
          </ul>
        ))}
    </div>
  );
}

export default FilteredProduct;
