import { useContext, useState } from "react";

export const Global = useContext();

export const GlobalProvide = ({ children }) => {
  const [Oldstate, Newstate] = useState([]);
  return (
    <Global.Provider value={[Oldstate, Newstate]}>
      {{ children }}
    </Global.Provider>
  );
};
