import React, { useState } from "react";
import "./Mails.css";
import DataEmails from "../Data/Emails";
function FilterEmail({ data }) {
  const [input, SetInput] = useState("");

  const [MyDataFiltered, setMyFilteredData] = useState([]);
  const HandelSearch = (e) => {
    SetInput(e.target.value);
    const FilterData = DataEmails.filter((v) => {
      return v.description.toLowerCase().includes(input.toLowerCase());
    });
    setMyFilteredData(FilterData);
  };
  return (
    <div>
      <input type="search" value={input} onChange={HandelSearch} />
      {MyDataFiltered.map((v) => (
        <div className="row">
          <div className="col">
            <div className="card">
              <img src="https://picsum.photos/200/300" alt="product" />
              <div className="card-header">{v.description}</div>
              <div className="card-body">{v.email}</div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default FilterEmail;
