import React, { useState } from "react";

function User() {
  const [state, setState] = useState([
    { id: 1, name: "mostafa" },
    { id: 1, name: "ahmed" },
    { id: 1, name: "mohamed" },
  ]);
  const [filtered, setfeleterd] = useState("");
  const HandelFilter = (e) => {
    const Myval = setfeleterd(e.target.value);
    const filtering = state.filter((v) => {
      return v.name === Myval;
    });
    setfeleterd(filtering);
  };

  return (
    <div>
      <input type="search" value={filtered} onChange={(e) => HandelFilter(e)} />
      {state.map((v) => (
        <div>
          <div>{v.name}</div>
        </div>
      ))}

      {JSON.stringify(filtered)}
    </div>
  );
}

export default User;
