import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {
  Card,
  CardBody,
  CardLink,
  CardSubtitle,
  CardText,
  Navbar,
  Spinner,
} from "reactstrap";

function FilterAddmember({ data, DeleteBTN }) {
  const [input, setinput] = useState("");
  const [dataFiltered, setdataFiltered] = useState([]);
  const Handelinput = (e) => {
    setinput(e.target.value.toLowerCase());
    if (data) {
      let mynewdata = data.filter((v) => {
        return v.name.toLowerCase().includes(input);
      });
      useEffect(() => {
        setdataFiltered(mynewdata);
      }, [dataFiltered, data]);
    }
  };
  return (
    <div>
      <input type="search" value={input} onChange={Handelinput} />

      <div className="Repo" style={{ color: "black" }}>
        {console.log(data)}
        <Navbar className="my-1" color="dark" dark>
          <Link to="/">Back to Home</Link>
        </Navbar>

        <div className="container">
          {data == null ? (
            <Spinner size="sm">
              <h3>Loading...Data</h3> <br /> <h4>No data To show</h4>
            </Spinner>
          ) : (
            <div>
              <ul className="unOrderList">
                {dataFiltered.map((v, i) => (
                  <Card
                    key={i}
                    style={{
                      width: "20rem",
                      height: "26rem",
                      backgroundColor: "black",
                      margin: "5px",
                      color: "whitesmoke",
                      wordBreak: "break-word",
                      letterSpacing: "1px",
                      textAlign: "center",
                      fontFamily: "arial",
                      fontSize: "15px",
                      borderRadius: "20px",
                      border: "2px solid gray",
                    }}
                  >
                    <CardBody
                      style={{ textAlign: "center", marginBottom: "50px" }}
                    >
                      <img
                        alt="Card cap"
                        className="img"
                        src={v.pic}
                        width="100%"
                      />
                      <CardText style={{ color: "blueviolet" }}>
                        {v.name}
                      </CardText>
                      <CardText>{v.Account}</CardText>

                      <CardSubtitle className="mb-2 text-muted" tag="h6">
                        {v.sfid}
                      </CardSubtitle>
                    </CardBody>
                    <CardBody>
                      <CardText>{v.email}</CardText>

                      <CardLink style={{ color: "gray" }}></CardLink>
                    </CardBody>
                    <button onClick={() => DeleteBTN(v)}>Delete</button>
                  </Card>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default FilterAddmember;
