import React, { useState } from "react";

function JuniperREF({ data }) {
  const [input, setinput] = useState("");
  const [FilterInput, SetfilterInput] = useState(data);

  const handleSearch = (e) => {
    const inputValue = e.target.value;
    setinput(inputValue);

    if (data) {
      const filterJuniper =
        inputValue === ""
          ? data.msg
          : data && data.msg.filter((v) => v.includes(inputValue));
      SetfilterInput(filterJuniper);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", options);
  };

  return (
    <div>
      <input
        type="search"
        value={input}
        onChange={handleSearch}
        placeholder="Junbier........"
      />
      {console.log(FilterInput.msg)}
      <table>
        <tbody>
          {Array.isArray(FilterInput) &&
            FilterInput.map((v, i) => (
              <tr key={i}>
                <td>{i + 1}</td>
                <td>{v.TicketNumber}</td>
                <td>{v.AgentName}</td>
                <td>{v.ProductType}</td>
                <td>{v.GuestName}</td>
                <td>{v.Supplier}</td>
                <td>{v.SupplierCategory}</td>
                <td>{v.JuniperREF}</td>
                <td>{v.SupplierReference}</td>
                <td>{formatDate(v.CheckInDate)}</td>
                <td>{formatDate(v.CheckOutDate)}</td>
                <td>{v.Vouchernumber}</td>
                <td>{v.SalesRate}</td>
                <td>{v.NetRate}</td>
                <td>{v.Currency}</td>
                <td>{v.ExchangeRate}</td>
                <td>{v.NetRateINKWD}</td>
                <td>{v.Profit}</td>
                <td>{v.Remarks}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}

export default JuniperREF;
