import React, { useReducer, useState } from "react";

function Qutation() {
  const [Qutation, Setqution] = useState([]);
  const Reducingdata = (prev, next) => ({ ...prev, ...next });

  const [
    {
      HotelName,
      CheckIn,
      CheckOut,
      NumberOfPax,
      Nationality,
      NumberOfUnits,
      RoomType,
      CancelationPolicy,
      Price,
      Note,
    },
    setData,
  ] = useReducer(Reducingdata, {
    HotelName: "",
    CheckIn: "",
    CheckOut: "",
    NumberOfPax: "",
    Nationality: "",
    NumberOfUnits: "",
    RoomType: "",
    CancelationPolicy: "",
    Price: "",
    Note: "",
  });
  const HandelChange = (e) => {
    setData({ [e.target.name]: e.target.value });
  };
  return (
    <div>
      <form>
        <input
          type="text"
          name=" HotelName"
          value={HotelName}
          placeholder="HotelName"
          onChange={HandelChange}
        />
        <input
          type="date"
          name="CheckIn"
          value={CheckIn}
          placeholder="CheckIn"
          onChange={HandelChange}
        />
        <input
          type="date"
          name="CheckOut"
          value={CheckOut}
          placeholder="CheckOut"
          onChange={HandelChange}
        />
        <input
          type="number"
          name="NumberOfPax"
          value={NumberOfPax}
          placeholder="NumberOfPax"
          onChange={HandelChange}
        />
        <input
          type="text"
          name="Nationality"
          value={Nationality}
          placeholder="Nationality"
          onChange={HandelChange}
        />
        <input
          type="number"
          name="NumberOfUnits"
          value={NumberOfUnits}
          placeholder="NumberOfUnits"
          onChange={HandelChange}
        />
        <input
          type="text"
          name="RoomType"
          value={RoomType}
          placeholder="RoomType"
          onChange={HandelChange}
        />
        <input
          type="text"
          name="CancelationPolicy"
          value={CancelationPolicy}
          placeholder="CancelationPolicy"
          onChange={HandelChange}
        />
        <input
          type="number"
          name="Price"
          value={Price}
          placeholder="Price"
          onChange={HandelChange}
        />
        <input
          type="text"
          name="Note"
          value={Note}
          placeholder="Note......."
          onChange={HandelChange}
        />
        <button>Submit</button>
      </form>
    </div>
  );
}

export default Qutation;
