import React, { useState } from "react";

function FilteredProduct({ data }) {
  const [input, setiunput] = useState("");
  const [mydata, setmydata] = useState(data);

  const HandelSearch = (e) => {
    setiunput(e.target.value);

    if (mydata) {
      const filteredItems = data.msg.filter((v) => {
        return v.description.includes(input);
      });
      console.log(filteredItems);
      setmydata(filteredItems);
    }
  };
  return (
    <div>
      <input type="search" value={input} onChange={HandelSearch} />
      {mydata && console.log(mydata)}
    </div>
  );
}

export default FilteredProduct;
