import React, { useContext, useEffect, useReducer, useState } from "react";
import { Global } from "../context/context";
import User from "./User";
function CardComponent() {
  const reduce = (prev, next) => ({ ...prev, ...next });
  const [Oldstate, Newstate] = useContext(Global);
  const [{ username, age }, setState] = useReducer(reduce, {
    username: "",
    age: "",
  });
  const ChangeHandeler = (e) => {
    const tag = e.target.name;
    const val = e.target.value;
    setState({ [tag]: val });
  };

  const HandelerSubmit = (e) => {
    e.preventDefault();
    if (username.trim().length === 0 || age.trim().length === 0 || +age < 1) {
      return;
    }
    Newstate([...Oldstate, { username, age }]);
    setState({ username: "", age: "" });
  };

  return (
    <div>
      <User
        data={data}
        HandelerSubmitfunc={HandelerSubmit}
        ChangeHandeler={ChangeHandeler}
        username={username}
        age={age}
      />
      <form onSubmit={HandelerSubmit}>
        <input
          type="text"
          name="username"
          value={username}
          onChange={ChangeHandeler}
        />
        <input type="text" name="age" value={age} onChange={ChangeHandeler} />
        <input type="submit" />
      </form>
      <h3>Welcome Mr {username}</h3>
    </div>
  );
}

export default CardComponent;
