import React, { useState } from "react";
import { Link } from "react-router-dom";
import {
  Card,
  CardBody,
  CardLink,
  CardSubtitle,
  CardText,
  CardTitle,
} from "reactstrap";
import "../App.css";

function FilteredProduct({ data }) {
  const [input, setInput] = useState("");
  const [myData, setMyData] = useState(data);
  const { msg } = data || [];
  const handleSearch = (e) => {
    const inputValue = e.target.value;
    setInput(inputValue);

    if (data) {
      const filteredItems = data.msg.filter((item) =>
        item.title.toLowerCase().includes(inputValue.toLowerCase())
      );
      setMyData({ msg: filteredItems });
    }
  };

  const cardTextStyle = {
    color: "whitesmoke",
    wordBreak: "break-word",
    textAlign: "center",
    fontFamily: "serif",
    fontSize: "15px",
  };

  const containerStyle = {
    display: "flex",
    flexWrap: "wrap",
    flexDirection: "row",
  };
  const searchStyle = {
    position: "fixed",
    top: "200px",
    left: "200px ",
    width: "20%",
    zIndex: 1 /* add z-index to ensure search bar is on top of other elements */,
  };

  return (
    <div style={containerStyle}>
      <input
        type="search"
        value={input}
        onChange={handleSearch}
        style={searchStyle}
      />
      {!msg && <h3>No data found</h3>}
      {msg &&
        msg.map((item) => (
          <ul className="unOrderList" key={item.Myid}>
            <Card
              style={{
                width: "21rem",
                height: "30rem",
                backgroundColor: "black",
                margin: "5px",
                color: "aqua",
                wordBreak: "break-word",
                textAlign: "center",
                fontFamily: "serif",
                fontSize: "15px",
                borderRadius: "20px",
                border: "2px solid gray",
                flex: "1 0 21rem",
              }}
            >
              <CardBody>
                <CardTitle tag="h5">#{item.Myid}</CardTitle>
                <CardSubtitle className="mb-2 text-muted" tag="h6">
                  {item.full_name}
                </CardSubtitle>
              </CardBody>

              <CardBody>
                <CardText style={cardTextStyle}>{item.title}</CardText>

                <CardText>{item.description}</CardText>
                <CardLink style={{ color: "gray" }}>
                  <Link to={`/${item.Myid}`} className="btn btn-primary">
                    Go Details
                  </Link>
                </CardLink>

                <CardText>createdOn: {item.createdOn}</CardText>
              </CardBody>
            </Card>
          </ul>
        ))}
    </div>
  );
}

export default FilteredProduct;
