import React, { useState } from "react";

function FilterByTicketNumber({ data }) {
  const [val, setVal] = useState("");
  const [filtered, setFiltered] = useState(data);

  const handleSearch = (e) => {
    const value = e.target.value;
    setVal(value);

    const filterTkt = data.filter((v) => {
      return String(v.TicketNumber) === value;
    });
    setFiltered(filterTkt);
  };

  return (
    <div>
      <input type="search" value={val} onChange={handleSearch} />
      {console.log(filtered)}
    </div>
  );
}

export default FilterByTicketNumber;
