import React, { useState } from "react";

function FilterAddmember({ data }) {
  const [input, setinput] = useState("");
  const [dataFiltered, setdataFiltered] = useState([]);
  const Handelinput = (e) => {
    setinput(e.target.value);
    {
      data === null && <h3>no data found</h3>;
    }

    let mynewdata = data.filter((v) => {
      return v.name.toUpperCase() === input.toUpperCase();
    });
    setdataFiltered(mynewdata);
  };
  return (
    <div>
      <input type="search" value={input} onChange={Handelinput} />
      {console.log(dataFiltered)}
    </div>
  );
}

export default FilterAddmember;
